custom.scss:
$primary: #F47A2A;

h1,
h2,
h3,
h4,
h5,
h6 {
  color: #000;
}

.search-by-vehicle {
  margin-top: -80px;
  padding-top: 130px;
}

.dmg-categories {
  padding: 50px 0;
}

.dmg-categories__inner {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 15px;
  @media (max-width: 767px) {
    grid-gap: 10px;
    grid-template-columns: repeat(2, 1fr);
  }
}

.dmg-category {
  position: relative;
  width: 100%;
  height: 300px;
  overflow: hidden;
  @media (max-width: 767px) {
    height: 260px;
  }
  &::after {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background: rgba(#000, 0.45);
    z-index: 2;
  }
  &:hover {
    img {
      transform: scale(1.1);
      filter: blur(3px);
    }
  }
  h3 {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    font-size: 30px;
    font-weight: 700;
    color: #fff;
    z-index: 3;
    margin: 0;
    padding: 15px;
  }
  img {
    position: absolute;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: all 0.3s ease-in-out;
    z-index: 1;
  }
}

.button--primary {
  color: stencilColor("button--primary-color");
  &:hover,
  &:focus,
  &:active {
    color: stencilColor("button--primary-color");
  }
  @media (min-width: 1025px) {
    &:hover,
    &:focus,
    &:active {
      color: stencilColor("button--primary-colorHover");
    }
  }
}

.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar {
  background-color: #6e6e6e;
}

@keyframes scroll {
  from {
    -webkit-transform: translateY(-100%);
    transform: translateY(-100%);
  }
  to {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }
}

.header--fixed {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  background: #fff;
  box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.1);
  animation: scroll 0.5s ease-in-out;
  z-index: 999;

  .logo__fixed {
    display: block;
  }
  .logo__regular {
    display: none;
  }
  .header__logo {
    margin-top: 0;
    margin-bottom: 0;
  }
  .search__body {
    height: 35px;
  }
  .header__navbar {
    min-height: auto;
  }
  .header__search {
    bottom: -20px;
  }
}

.logo__fixed {
  display: none;
  max-width: 90px;
  height: 80px;
}

.card-body .card-title {
  min-height: 44px;
}

.main-shop-banner {
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #222;
  color: #fff;
  text-align: center;
  padding: 40px 0;
  .page-heading {
    color: $primary;
    margin: 0 0 15px;
  }
  p {
    padding: 0 0 15px;
  }
  @media (max-width: 767px) {
    br {
      display: none;
    }
  }
}

.search-by-vehicle {
  .page-heading {
    text-align: center;
  }
}
