pages/account/orders/completed:
---
customer:
orders:
limit: 3
---
{{#partial "page"}}
<div class="themevale_breadcrumb">
    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    <h2 class="account-heading title">{{lang 'account.orders.heading' }}</h2>
</div>
<main class="page ">
    {{> components/account/navigation account_page='orders'}}
    <div class="account gift_certificate-page">
        <div class="account-row orders-row">
            <section class="orders">
                {{> components/account/orders-list orders=customer.orders}}
                {{> components/common/small-paginator pagination.orders}}
            </section>
        </div>
    </div>
</main>
{{/partial}}
{{> layout/base}}

components/common/navigation-list-altern:
{{#if children}}
    <p class="navPages-action navPages-action-depth-max has-subMenu is-root">
        <a class="text" href="{{url}}">{{name}}</a>
        <span class="navPages-action-moreIcon" aria-hidden="true">
            <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
        </span>
    </p>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action" href="{{url}}"><span class="text">{{name}}</span></a>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank" rel="noopener">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}
