pages/custom/product/product-layout-3:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
products:
    featured:
        limit: 6
---
{{#partial "page"}}
    <div class="themevale_breadcrumb">
        {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    </div>


    <div class="page"> 
        <aside class="page-sidebar">
            {{> components/themevale/sidebar/product-sidebar}}
        </aside>
        <main class="page-content product-layout-3" id="product-listing-container">
            {{#each product.shipping_messages}}
                {{> components/common/alert-info message}}
            {{/each}}
            <div class="themevale_sidebar-mobile-wrapper">
                <div class="item item-iconSidebar text-left">
                    <div class="themevale_sidebar-mobile">
                        <span class='text'>sidebar:</span>
                        <svg class="icon"><use xlink:href='#sidebar'></use></svg>
                    </div>
                </div>
            </div>
            <div itemscope itemtype="http://schema.org/Product">
                {{> components/products/product-view-3 schema=true }}
                {{> components/themevale/product/product-description-3}}
            </div>
        </main>
    </div>
{{/partial}}
{{> layout/base}}

components/products/reviews-2:
{{#if reviews.total}}
    <ul class="productReviews-list" id="productReviews-list">
        {{#each reviews.list}}
        <li class="productReview">
            <article itemprop="review" itemscope itemtype="http://schema.org/Review">
                <header>
                    <h5 itemprop="name" class="productReview-title">{{ title }}</h5>
                    <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="http://schema.org/Rating">
                        {{> components/products/ratings rating=rating}}
                        <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                    </span>
                    {{#if name}}
                        <meta itemprop="author" content="{{name}}">
                        <p class="productReview-author">
                            {{lang 'products.reviews.post_on_by' date=date name=name }}
                        </p>
                    {{else}}
                        <p class="productReview-author">
                            {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                        </p>
                    {{/if}}
                </header>
                <p itemprop="reviewBody" class="productReview-body">{{nl2br text}}</p>
            </article>
        </li>
        {{/each}}
    </ul>
    {{> components/common/paginator pagination.reviews}}
    <div class="btn-createReview">
        <a class="button button--border" href="#" data-reveal-id="modal-review-form">{{lang 'products.reviews.write_a_review'}}</a>
    </div>
    <div class="clearfix"></div>
{{else}}
    <div class="alert alert-warning">
        This product hasn't received any reviews yet. Be the first to review this product!
    </div>
    <div class="btn-createReview">
        <a class="button button--border" href="#" data-reveal-id="modal-review-form">{{lang 'products.reviews.write_a_review'}}</a>
    </div>
{{/if}}
{{{snippet 'reviews'}}}

components/themevale/product/countdown:
{{#each product.custom_fields}}
    {{#if name '==' 'countdown'}}
        <div class="countDown countdown_{{../../product.id}} hide">
            <div class="countdownValue_{{../../product.id}} countdownValue">
                {{{value}}}
            </div>
            <p id="countDownDate_{{../../product.id}}" class="countDownDate_{{../../product.id}}"></p>
        </div>
    {{/if}}
{{/each}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star"/>
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition. 
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price schema_org=schema_org}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
            {{theme_settings.pdp-retail-price-label}}
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax non-sale-price--withTax {{#unless price.non_sale_price_without_tax}}price-none{{/unless}}" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            <span data-product-non-sale-price-with-tax class="price price--non-sale">
                {{price.non_sale_price_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="url" content="{{product.url}}">
                <meta itemprop="availability" content="{{product.availability}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="priceValidUntil" content="2020-03-25">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.with_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="true">
                </div>
            {{/if}}
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
            <span data-product-rrp-price-without-tax class="price price--rrp"> 
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} {{#unless price.non_sale_price_without_tax}}price-none{{/unless}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            <span data-product-non-sale-price-without-tax class="price price--non-sale">
                {{price.non_sale_price_without_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withoutTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">{{price.without_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="url" content="{{product.url}}">
                <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability"
                    content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="priceValidUntil" content="2020-03-25">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.without_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="false">
                </div>
            {{/if}}
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <!-- <span class="price">{{lang 'products.you_save_closing_bracket'}}</span> -->
         </div>
    {{/if}}
{{/and}}

components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
    <dd class="productView-info-value">
        <a href="{{url}}"
           {{#unless is_ajax }}data-reveal-id="bulkPricingModal{{#if id}}-{{id}}-{{$index}}{{/if}}"{{/unless}}>
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="bulkPricingModal{{#if id}}-{{id}}-{{$index}}{{/if}}" class="modal modal--small" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            <a href="#" class="modal-close" aria-label="Close"><span aria-hidden="true">&#215;</span></a>
        </div>
        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
                {{#each bulk_discount_rates}}
                <li>
                    {{lang 'products.bulk_pricing.range' min=min max=max}}
                    {{#if type '===' 'percent'}}
                    {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                    {{/if}}
                    {{#if type '===' 'fixed'}}
                    {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                    {{/if}}
                    {{#if type '===' 'price'}}
                    {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                    {{/if}}
                </li>
                {{/each}}
            </ul>
        </div>
    </div>
{{/if}}

